public class RC4 {

    public static void main(String[] args) {
        RC4 rc4 = new RC4();

        String plaintext = "helloworld";
        String key = "key";

        String ciphertext = rc4.encrypt(plaintext, key);
        String decryptText = rc4.encrypt(ciphertext, key);

