```java
public boolean duplicate(int numbers[],int length,int [] duplication) {
    if(numbers == null || length <= 0){
        return false;
    }
    for (int i = 0; i < length; i++){
        int index = numbers[i];
        if (index < 0){
            index += length;
        }
        if (numbers[index] < 0){
            duplication[0] = index;
            return true;
        }
        numbers[index] -= length;
    }
    return false;
